import React from "react";
import "../common/style/table.css";

// import './Table.css'; // Import your CSS file for styling

const Table = () => {
  const tableData = [
    [
      "01",
      "www.google.com",
      "QB0GaK7",
      ["Select Tags", "Tag 1", "Tag 2", "Tag 3", "Tag 4"],
      "",
    ],
    [
      "02",
      "timesonline.co.uk",
      "Z9I2o9o",
      ["Select Tags", "Tag 1", "Tag 2", "Tag 3", "Tag 4"],
      "",
    ],
    [
      "03",
      "newyorker.com",
      "pW44149",
      ["Select Tags", "Tag 1", "Tag 2", "Tag 3", "Tag 4"],
      "",
    ],
    [
      "04",
      "angelfire.com",
      "c1TO1x6",
      ["Select Tags", "Tag 1", "Tag 2", "Tag 3", "Tag 4"],
      "",
    ],
    [
      "05",
      "state.gov",
      ["Select Tags", "nh294TY", "Tag 1", "Tag 2", "Tag 3", "Tag 4"],
      "",
    ],
  ];
  const handleTagChange = (id, value) => {
    const updatedTableData = tableData.map((row) => {
      if (row.id === id) {
        return { ...row, selectedTag: value };
      }
      return row;
    });

    // Update the state or perform any other necessary action with updatedTableData
    console.log(updatedTableData);
  };
  return (
    // <div>
    //   <span style={{marginTop: "50px"}}>
    //     <h3>Upload</h3>
    //   </span>
    // </div>
    <table className="custom-table">
      <thead>
        <tr>
          <th>SL No</th>
          <th>Links</th>
          <th>Prefix</th>
          <th>Add Tags</th>
          <th>Selected Tags</th>
        </tr>
      </thead>
      <tbody>
        {tableData.map((row) => (
          <tr key={row.id}>
            <td>{row.id}</td>
            <td>{row.link}</td>
            <td>{row.prefix}</td>
            <td>
              <select
                value={row.selectedTag}
                onChange={(e) => handleTagChange(row.id, e.target.value)}
              >
                {row.tags.map((tag, index) => (
                  <option key={index} value={tag}>
                    {tag}
                  </option>
                ))}
              </select>
            </td>
            <td>{row.selectedTag}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
